Ext.define('FrontHPApp.view.Tab2.window.FrmBrowseFileCheckViewController', {
    extend: 'Ext.app.ViewController',
    alias: 'controller.tab2windowfrmbrowsefilecheck',

    onInsertPaperClick: function(button, e, eOpts) {
        Console.log("เพิ่มการแนบเอกสาร");
    },

    onClearClick: function(button, e, eOpts) {
        var me = this.getView(),
            formBrowseFile = me.down('#formBrowseFile');
        formBrowseFile.reset();
    },

    onSaveClick: function(button, e, eOpts) {
        console.log("เพิ่มข้อมูลเอกสาร");
    },

    onEditClick: function(button, e, eOpts) {
        console.log("แก้ไขข้อมูลเอกสาร");
    },

    onDeleteClick: function(button, e, eOpts) {
        var me = this.getView(),
            grid = me.down('gridpanel'),
            store = grid.getStore(),
            record = grid.getSelection(),
            count = record.length,
            ids = [],
            msg ="";
        console.log(count);
        if(count > 0){


            Ext.MessageBox.confirm("Confirm","คุณต้องการลบรายการ",function(btn){

                if(btn == 'yes'){
                    for(i=0;i<count;i++){

                        msg += record[i].get('IDNum')+"<br>";
                        console.log(msg);

                        ids.push(record[i].get('IDNum'));

                        store.remove(record[i]);
                        console.log(store);
                    }

                }
                console.log(ids);
                Ext.MessageBox.alert("Status",msg);

            },this);

        }
    },

    onGridBrowseFileSelectionChange: function(model, selected, eOpts) {
        // PanelBrowseFile
        var me = this.getView(),
            formBrowseFile = me.down("#formBrowseFile").getForm(); //itemid
            if(selected[0]){
                formBrowseFile.loadRecord(selected[0]);
        }
        else
        {
            formBrowseFile.reset();
        }
    }

});
